import { Component } from '@angular/core';
import { Internacao } from 'src/app/models/internacao/internacao';
import { Leito } from 'src/app/models/leito/leito';
import { Medico } from 'src/app/models/medico/medico';
import { Paciente } from 'src/app/models/paciente/paciente';
import { Usuario } from 'src/app/models/usuario/usuario';
import { InternacaoService } from 'src/app/services/internacao/internacao.service';
import { LeitoService } from 'src/app/services/leito/leito.service';
import { MedicoService } from 'src/app/services/medico/medico.service';
import { PacienteService } from 'src/app/services/paciente/paciente.service';

@Component({
  selector: 'app-internacao',
  templateUrl: './internacao.component.html',
  styleUrls: ['./internacao.component.css']
})
export class InternacaoComponent {
  internacoes: Internacao[] = [];
  novaInternacao: Internacao = new Internacao();

  pacientes: Paciente[] = [];
  leitos: Leito[] = [];
  medicos: Medico[] = [];

  constructor(private pacienteService: PacienteService,
    private medicoService: MedicoService,
    private leitoService: LeitoService,
    private internacaoService: InternacaoService) { }

  ngOnInit(): void {
    this.carregarInternacoes();
    this.carregarPacientes();
    this.carregarLeitos();
    this.carregarMedicos();
  }

  carregarPacientes(): void {
    this.pacienteService.list()
      .subscribe(pacientes => this.pacientes = pacientes);
  }

  carregarLeitos(): void {
    this.leitoService.list()
      .subscribe(leitos => this.leitos = leitos);
  }

  carregarMedicos(): void {
    this.medicoService.list()
      .subscribe(medicos => this.medicos = medicos);
  }

  carregarInternacoes(): void {
    this.internacaoService.list()
      .subscribe(internacoes => this.internacoes = internacoes);
  }

  adicionarInternacao(): void {
    this.internacaoService.adicionarInternacao(this.novaInternacao).subscribe(internacao => {
      this.novaInternacao = new Internacao();
      this.carregarInternacoes();
    });
  }

  editarInternacao(): void {
    this.internacaoService.editarInternacao(this.novaInternacao).subscribe(internacao => {
      this.novaInternacao = new Internacao();
      this.carregarInternacoes();
    });
  }

  preparaEdicao(internacao: Internacao): void {
    this.novaInternacao = new Internacao();
    this.novaInternacao.id = internacao.id;
    this.novaInternacao.diagnostico = internacao.diagnostico;
    this.novaInternacao.paciente = internacao.paciente;
    this.novaInternacao.leito = internacao.leito;
    this.novaInternacao.medico = internacao.medico;
  }

  preparaExclusao(internacao: Internacao): void {
    this.novaInternacao = internacao;
  }

  excluirInternacao(internacao: Internacao): void {
    this.internacaoService.excluirInternacao(internacao).subscribe((internacao: Internacao) => {
      this.novaInternacao = new Internacao();
      this.carregarInternacoes();
    });
  }


}
<h2>Internações</h2>

<table class="table table-striped">
  <thead>
    <tr>
      <th>ID</th>
      <th>Data de Entrada</th>
      <th>Data de Saída</th>
      <th>Diagnóstico</th>
      <th>Leito</th>
      <th>Médico</th>
      <th>Paciente</th>
      <th>Ações</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let internacao of internacoes">
      <td>{{ internacao.id }}</td>
      <td>{{ internacao.dataEntrada }}</td>
      <td>{{ internacao.dataSaida }}</td>
      <td>{{ internacao.diagnostico }}</td>
      <td>{{ internacao.leito.numeroSala }}</td>
      <td>{{ internacao.medico.usuario.nome }}</td>
      <td>{{ internacao.paciente.nome }}</td>
      <td>
        <button type="button" class="btn btn-primary btn-sm" data-bs-toggle="modal" data-bs-target="#modalEditarInternacao" (click)="preparaEdicao(internacao)">Editar</button>
        <button type="button" class="btn btn-danger btn-sm" data-bs-toggle="modal" data-bs-target="#modalExcluirInternacao" (click)="preparaExclusao(internacao)">Excluir</button>
      </td>
    </tr>
  </tbody>
</table>

<!-- Botão trigger modal -->
<button type="button" class="btn btn-success" data-bs-toggle="modal" data-bs-target="#modalAdicionarInternacao">
  Adicionar Internação
</button>

<button routerLink="/leito" style="margin-left: 10px;" type="button" class="btn btn-primary">
    Cadastro de Leitos
  </button>

<!-- Modal Adicionar Internação -->
<div class="modal fade" id="modalAdicionarInternacao" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Adicionar Nova Internação</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form (submit)="adicionarInternacao()">
          <div class="form-group">
            <label for="diagnostico" class="form-label">Diagnóstico:</label>
            <input name="diagnostico" type="text" class="form-control" id="diagnosticoAd" [(ngModel)]="novaInternacao.diagnostico" required>
          </div>
          <div class="form-group">
            <label for="leito" class="form-label">Leito:</label>
            <select name="leito" class="form-control" id="leitoAd" [(ngModel)]="novaInternacao.leito" required>
              <option *ngFor="let leito of leitos" [value]="leito">{{ novaInternacao.leito.numeroSala }}</option>
            </select>
          </div>
          <div class="form-group">
            <label for="medico" class="form-label">Médico:</label>
            <select name="medico" class="form-control" id="medicoAd" [(ngModel)]="novaInternacao.medico" required>
              <option *ngFor="let medico of medicos" [value]="medico">{{ novaInternacao.medico.usuario.nome }}</option>
            </select>
          </div>
          <div class="form-group">
            <label for="paciente" class="form-label">Paciente:</label>
            <select name="paciente" class="form-control" id="pacienteAd" [(ngModel)]="novaInternacao.paciente" required>
              <option *ngFor="let paciente of pacientes" [value]="paciente">{{ novaInternacao.paciente.nome }}</option>
            </select>
          </div>
          <div class="modal-footer">
            <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Cancelar</button>
            <button type="submit" class="btn btn-success">Adicionar</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>

<!-- Modal Editar Internação -->
<div class="modal fade" id="modalEditarInternacao" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Editar Internação</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form>
          <div class="form-group">
            <label for="diagnostico" class="form-label">Diagnóstico:</label>
            <input name="diagnostico" type="text" class="form-control" id="diagnosticoEd" [(ngModel)]="novaInternacao.diagnostico" required>
          </div>
          <div class="form-group">
            <label for="leito" class="form-label">Leito:</label>
            <select name="leito" class="form-control" id="leitoEd" [(ngModel)]="novaInternacao.leito" required>
              <option *ngFor="let leito of leitos" [value]="leito">{{ leito.numeroSala }}</option>
            </select>
          </div>
          <div class="form-group">
            <label for="medico" class="form-label">Médico:</label>
            <select name="medico" class="form-control" id="medicoEd" [(ngModel)]="novaInternacao.medico" required>
              <option *ngFor="let medico of medicos" [value]="medico">{{ medico.usuario.nome }}</option>
            </select>
          </div>
          <div class="form-group">
            <label for="paciente" class="form-label">Paciente:</label>
            <select name="paciente" class="form-control" id="pacienteEd" [(ngModel)]="novaInternacao.paciente" required>
              <option *ngFor="let paciente of pacientes" [value]="paciente">{{ paciente.nome }}</option>
            </select>
          </div>
          <div class="modal-footer">
            <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Cancelar</button>
            <button type="submit" class="btn btn-primary" data-bs-dismiss="modal" (click)="editarInternacao()">Editar</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>

<!-- Modal Excluir Internação -->
<div class="modal fade" id="modalExcluirInternacao" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Excluir Internação</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
          <p>Tem certeza que deseja excluir a Internação selecionada?</p>
          <div class="modal-footer">
            <button type="button" class="btn btn-primary" data-bs-dismiss="modal">Cancelar</button>
            <button type="button" class="btn btn-danger" data-bs-dismiss="modal"  (click)="excluirInternacao(novaInternacao)">Excluir</button>
          </div>
      </div>
    </div>
  </div>
</div>
