import { Component } from '@angular/core';
import { Usuario } from 'src/app/models/usuario/usuario';
import { UsuarioService } from 'src/app/services/usuario/usuario.service';

@Component({
  selector: 'app-usuario',
  templateUrl: './usuario.component.html',
  styleUrls: ['./usuario.component.css']
})
export class UsuarioComponent {
  usuarios: Usuario[] = [];
  novoUsuario: Usuario = new Usuario();
  exibirModal: boolean = false;
  constructor(private usuarioService:UsuarioService) { }

  ngOnInit(): void {
    this.carregarUsuarios();
  }
  carregarUsuarios(): void {
    this.usuarioService.list()
    .subscribe(usuarios => this.usuarios = usuarios);
  }

  adicionarUsuario(): void {
    this.usuarioService.adicionarUsuario(this.novoUsuario).subscribe(usuario => {
      this.usuarios.push(usuario);
      this.novoUsuario = new Usuario(); // Limpa os campos do formulário após adicionar o usuário
      this.fecharModal(); // Fecha o modal após adicionar o usuário
    });
  }

  abrirModal(): void {
    this.exibirModal = true;
  }

  fecharModal(): void {
    this.exibirModal = false;
  }

  editarUsuario(usuario: Usuario): void {
    // Lógica para editar o usuário com base no objeto "usuario"
  }

  excluirUsuario(usuario: Usuario): void {
    // Lógica para excluir o usuário com base no objeto "usuario"
  }
}
<h2>Usuários</h2>

<!-- Modal -->

<form (submit)="adicionarUsuario()">
  <div class="form-group">
    <label for="nome">Nome:</label>
  <input type="text" class="form-control" id="nome" [(ngModel)]="novoUsuario.nome" required>
  </div>
  <div class="form-group">
    <label for="cpf">CPF:</label>
    <input type="text" class="form-control" id="cpf" [(ngModel)]="novoUsuario.cpf" required>
  </div>
  <div class="form-group">
    <label for="permissao">Permissão:</label>
    <input type="text" class="form-control" id="permissao" [(ngModel)]="novoUsuario.permissao" required>
   </div>
  <button type="submit" class="btn btn-primary">Adicionar</button>
</form>

<table class="table table-striped">
  <thead>
    <tr>
      <th>ID</th>
      <th>Nome</th>
      <th>CPF</th>
      <th>Permissão</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let usuario of usuarios">
      <td>{{ usuario.id }}</td>
      <td>{{ usuario.nome }}</td>
      <td>{{ usuario.cpf }}</td>
      <td>{{ usuario.permissao }}</td>
      <td>
        <button class="btn btn-primary btn-sm" (click)="editarUsuario(usuario)">Editar</button>
        <button class="btn btn-danger btn-sm" (click)="excluirUsuario(usuario)">Excluir</button>
      </td>
    </tr>
  </tbody>
</table>
<button class="btn btn-primary" (click)="abrirModal()">Adicionar Usuário</button>
