import { Component } from '@angular/core';
import { Exame } from 'src/app/models/exame/exame';
import { ExameService } from 'src/app/services/exame/exame.service';

@Component({
  selector: 'app-exame',
  templateUrl: './exame.component.html',
  styleUrls: ['./exame.component.css']
})
export class ExameComponent {
  exames: Exame[] = [];
  novoExame: Exame = new Exame();

  constructor(private exameService:ExameService) { }

  ngOnInit(): void {
    this.carregarExames();
  }

  carregarExames(): void {
    this.exameService.list()
      .subscribe(exames => this.exames = exames);
  }

  adicionarExame(): void {
    this.exameService.adicionarExame(this.novoExame).subscribe(exame => {
      this.novoExame = new Exame();
      this.carregarExames();
    });
  }

  editarExame(): void {
    this.exameService.editarExame(this.novoExame).subscribe(exame => {
      this.novoExame = new Exame();
      this.carregarExames();
    });
  }

  preparaEdicao(exame: Exame): void {
    this.novoExame = new Exame();
    this.novoExame.id = exame.id;
    this.novoExame.tipo = exame.tipo;
    this.novoExame.descricao = exame.descricao;
  }

  preparaExclusao(exame: Exame): void {
    this.novoExame = exame;
  }

  excluirExame(exame: Exame): void {
    this.exameService.excluirExame(exame).subscribe((exame: Exame) => {
      this.novoExame = new Exame();
      this.carregarExames();
    });
  }

}
<h2>Exames</h2>

<table class="table table-striped">
  <thead>
    <tr>
      <th>ID</th>
      <th>Descrição</th>
      <th>Tipo</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let exame of exames">
      <td>{{ exame.id }}</td>
      <td>{{ exame.descricao }}</td>
      <td>{{ exame.tipo }}</td>
      <td>
        <button type="button" class="btn btn-primary btn-sm" data-bs-toggle="modal" data-bs-target="#modalEditarExame" (click)="preparaEdicao(exame)">Editar</button>
        <button type="button" class="btn btn-danger btn-sm" data-bs-toggle="modal" data-bs-target="#modalExcluirExame" (click)="preparaExclusao(exame)">Excluir</button>
      </td>
    </tr>
  </tbody>
</table>

<!-- Botão para abrir o modal de adicionar exame -->
<button type="button" class="btn btn-success" data-bs-toggle="modal" data-bs-target="#modalAdicionarExame">
  Adicionar Exame
</button>

<!-- Modal de adicionar exame -->
<div class="modal fade" id="modalAdicionarExame" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Adicionar Exame</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form (submit)="adicionarExame()">
          <div class="form-group">
            <label for="descricao" class="form-label">Descrição:</label>
            <input name="descricao" type="text" class="form-control" id="descricao" [(ngModel)]="novoExame.descricao" required>
          </div>
          <div class="form-group">
            <label for="tipo" class="form-label">Tipo:</label>
            <input name="tipo" type="text" class="form-control" id="tipo" [(ngModel)]="novoExame.tipo" required>
          </div>
          <div class="modal-footer">
            <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Cancelar</button>
            <button type="submit" class="btn btn-success">Salvar</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>

<!-- Modal de editar exame -->
<div class="modal fade" id="modalEditarExame" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Editar Exame</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form (submit)="editarExame()">
          <div class="form-group">
            <label for="descricao" class="form-label">Descrição:</label>
            <input name="descricao" type="text" class="form-control" id="descricao" [(ngModel)]="novoExame.descricao" required>
          </div>
          <div class="form-group">
            <label for="tipo" class="form-label">Tipo:</label>
            <input name="tipo" type="text" class="form-control" id="tipo" [(ngModel)]="novoExame.tipo" required>
          </div>
          <div class="modal-footer">
            <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Cancelar</button>
            <button type="submit" class="btn btn-primary" data-bs-dismiss="modal">Editar</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>

<!-- Modal de excluir exame -->
<div class="modal fade" id="modalExcluirExame" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Excluir Exame</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <p>Tem certeza que deseja excluir o exame selecionado?</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-primary" data-bs-dismiss="modal">Cancelar</button>
        <button type="button" class="btn btn-danger" data-bs-dismiss="modal" (click)="excluirExame(novoExame)">Excluir</button>
      </div>
    </div>
  </div>
</div>
