import { Component } from '@angular/core';
import { Usuario } from 'src/app/models/usuario/usuario';
import { UsuarioService } from 'src/app/services/usuario/usuario.service';

@Component({
  selector: 'app-usuario',
  templateUrl: './usuario.component.html',
  styleUrls: ['./usuario.component.css']
})
export class UsuarioComponent {
  usuarios: Usuario[] = [];
  novoUsuario: Usuario = new Usuario();

  constructor(private usuarioService: UsuarioService) { }

  ngOnInit(): void {
    this.carregarUsuarios();
  }
  carregarUsuarios(): void {
    this.usuarioService.list()
      .subscribe(usuarios => this.usuarios = usuarios);
  }

  adicionarUsuario(): void {
    this.usuarioService.adicionarUsuario(this.novoUsuario).subscribe(usuario => {
      this.usuarios.push(usuario);
      this.novoUsuario = new Usuario();
      this.carregarUsuarios();
    });
  }

  editarUsuario(usuario: Usuario): void {
    // Lógica para editar o usuário com base no objeto "usuario"
  }

  excluirUsuario(usuario: Usuario): void {
    // Lógica para excluir o usuário com base no objeto "usuario"
  }
}
